import { TextField } from "@mui/material";
import { Box } from "@mui/system";
import { useState } from "react";

export default function GenericInputField(props) {
  const { label, helperText, maxLength, type, validate = false } = props;

  const [inputValue, setinputValue] = useState("");
  const [errorStatus, seterrorStatus] = useState(false);

  const validateInput = (pattern, input) => {
    if (validate) {
      var validInput = pattern.test(input);
    }
    return validInput;
  };

  const onChangeHandler = (e = {}) => {
    let targetValue = e?.target?.value;
    if (targetValue <= maxLength) {
      let Alphacharregex = /^[a-zA-Z]+$/;
      let emailRegex = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;

      let validInput = validateInput(
        type == "onlyAlpha" ? Alphacharregex : emailRegex,
        targetValue
      );
      if (validInput) {
        setinputValue(targetValue);
        seterrorStatus(false);
      } else {
        setinputValue(targetValue);
        seterrorStatus(true);
      }
    }
  };

  return (
    <Box sx={{ p: 1 }}>
      <TextField
        fullWidth
        error={errorStatus}
        id={label}
        label={label}
        variant="outlined"
        helperText={errorStatus ? helperText : ""}
        onChange={onChangeHandler}
        value={inputValue}
      />
    </Box>
  );
}
