import { TextField } from "@mui/material";
import { Box } from "@mui/system";
import { useState } from "react";

export default function GenericInputField(props) {
  const { label, helperText, maxLength, type, validate = false } = props;

  const [inputValue, setinputValue] = useState("");
  const [errorStatus, seterrorStatus] = useState(false);
  const validateInput = (pattern, input) => {
    if (pattern.test(input)) {
      setinputValue(input);
    } else {
      seterrorStatus(true);
    }
  };
  const onChangeHandler = (e = {}) => {
    let Alphacharregex = /^[a-zA-Z]+$/;
    let emailRegex = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;
    let targetValue = e?.target?.value;
    if (validate) {
      let validInput =
        type == "onlyAlpha"
          ? Alphacharregex.test(e?.target?.value)
          : emailRegex.test(e?.target?.value);
      if (validInput) {
        setinputValue(targetValue);
      } else {
        seterrorStatus(true);
        setinputValue(targetValue.substring(0, targetValue.length() - 1));
      }
    } else {
      setinputValue(targetValue);
    }
  };
  return (
    <Box sx={{ p: 1 }}>
      <TextField
        fullWidth
        error={errorStatus}
        id={label}
        label={label}
        variant="outlined"
        helperText={errorStatus ? helperText : ""}
        onChange={onChangeHandler}
        value={inputValue}
      />
    </Box>
  );
}
