import { TextField } from "@mui/material";
import { Box } from "@mui/system";
import { useState } from "react";

export default function GenericInputField(props) {
  const { label, helperText, maxLength, type, validate = false } = props;

  const [inputValue, setinputValue] = useState("");
  const [errorStatus, seterrorStatus] = useState(false);
  const onChangeHandler = (e = {}) => {
    let Alphacharregex = /^[a-zA-Z]+$/;
    let emailRegex = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;
    //return emailRegex.test(email);
    //console.log("entered value", e.target.value);
    let finalValue;
    // validate
    //   ? type === "onlyAlpha"
    //     ? Alphacharregex.test(e?.target?.value)
    //       ? e?.target?.value
    //       : ""
    //     :
    //   : e?.target?.value;

    switch (type) {
      case "onlyAlpha":
        console.log("onlyAlpha");
        finalValue = Alphacharregex.test(e?.target?.value)
          ? e?.target?.value
          : seterrorStatus(true);
        setinputValue(finalValue);
        break;
      case "email":
        console.log("email");
        finalValue = emailRegex.test(e?.target?.value)
          ? e?.target?.value
          : seterrorStatus(true);
        setinputValue(finalValue);
        break;
      default:
        console.log("default");
        finalValue = e?.target?.value;
        setinputValue(finalValue);
    }

    setinputValue(finalValue);
  };
  return (
    <Box sx={{ p: 1 }}>
      <TextField
        fullWidth
        error={errorStatus}
        id={label}
        label={label}
        variant="outlined"
        helperText={errorStatus ? helperText : ""}
        onChange={onChangeHandler}
        value={inputValue}
      />
    </Box>
  );
}
